import React from 'react'
import {useParams} from 'react-router-dom'
import {ContactContext} from '../Helper/Context'

const EditContact = () => {
    const {id } = useParams();
    const {contactList,setContactList} = useContext(ContactContext)

    const loaddata=(id)=>{
        const data = contactList.filter(list=>list.id = id)
        return data
    }
    return (
        <div className="container">           
            <form>
                <div class="form-group">
                    <label>Full Name</label>
                    <input type="text" class="form-control" name="fname"  value={data.fname}
                    />                
                </div>
                <div class="form-group">
                    <label>Phone Number</label>
                    <input type="number" class="form-control" name="phone"  value={data.phone}
                     />                
                </div>
                <div class="form-group">
                    <label>Email address</label>
                    <input type="email" class="form-control" name="email" value={data.email}
                    aria-describedby="emailHelp" />                
                </div>

            <button type="submit" class="btn btn-primary">Submit</button>
            </form>

        </div>
    )
}

export default EditContact
