import React ,{useContext,useState} from 'react'
import {useParams} from 'react-router-dom'
import {ContactContext} from '../Helper/Context'

const EditContact = () => {
    const {id } = useParams();
    const {contactList,setContactList} = useContext(ContactContext)
    const [input,setInput]= useState({
        fname:"",
        phone:"",
        email:""
    })

    const ChangeHandeler=(e)=>{
        const {name, value} = e.target;
        setInput({
            ...input,[name]:value,
        });

    }

        const datacon = contactList.find(list=>list.id = id)
        console.log(datacon)

    return (
        <div className="container">           
            <form>
                <div class="form-group">
                    <label>Full Name</label>
                    <input type="text" class="form-control" name="fname"   value={datacon.fullname}
                    onChange={ChangeHandeler} />                
                </div>
                <div class="form-group">
                    <label>Phone Number</label>
                    <input type="number" class="form-control" name="phone"  
                     onChange={ChangeHandeler}/>                
                </div>
                <div class="form-group">
                    <label>Email address</label>
                    <input type="email" class="form-control" name="email" 
                     onChange={ChangeHandeler} aria-describedby="emailHelp" />                
                </div>

            <button type="submit" class="btn btn-primary">Submit</button>
            </form>

        </div>
    )
}

export default EditContact
